import request from '@/utils/request';
export function getRoutes() {
  return request({
    url: '/routes',
    method: 'get'
  });
}
export function getRoles() {
  return request({
    url: '/roles',
    method: 'get'
  });
}
export function addRole(data) {
  return request({
    url: '/role',
    method: 'post',
    data: data
  });
}
export function updateRole(id, data) {
  return request({
    url: "/role/".concat(id),
    method: 'put',
    data: data
  });
}
export function deleteRole(id) {
  return request({
    url: "/role/".concat(id),
    method: 'delete'
  });
}
